(function (app) {
	app
		.directive("drawing", function () {
			return {
				restrict: "A",
				scope: {
					bw: "=width",
					bh: "=height"
				},
				link: function (scope, element) {
					var ctx = element[0].getContext('2d');
					console.log(element);
					// variable that decides if something should be drawn on mousemove
					var drawing = false;

					// the last coordinates before the current move
					var lastX;
					var lastY;

					var bw = scope.bw;
					var bh = scope.bh;
					var p = 5;


					function drawBoard() {
						for (var x = 0; x <= bw; x += 30) {
							ctx.moveTo(x + p, p);
							ctx.lineTo(x + p, bh + p);
						}


						for (var x = 0; x <= bh; x += 30) {
							ctx.moveTo(p, x + p);
							ctx.lineTo(bw + p, x + p);
						}

						ctx.strokeStyle = "lightgray";
						ctx.stroke();
					}

					drawBoard();
					function start_move(event) {

						if (event.offsetX !== undefined) {
							lastX = event.offsetX;
							lastY = event.offsetY;

						} else { // Firefox compatibility
							lastX = event.layerX - event.currentTarget.offsetLeft;
							lastY = event.layerY - event.currentTarget.offsetTop;

						}
						console.log(lastX);
						console.log(lastY);
						// begins new line
						ctx.beginPath();

						drawing = true;
					}

					element.bind('mousedown', start_move);

					function move(event) {
						if (drawing) {
							// get current mouse position
							if (event.offsetX !== undefined) {
								currentX = event.offsetX;
								currentY = event.offsetY;
							} else {
								currentX = event.layerX - event.currentTarget.offsetLeft;
								currentY = event.layerY - event.currentTarget.offsetTop;
							}

							draw(lastX, lastY, currentX, currentY);

							// set current coordinates to last one
							lastX = currentX;
							lastY = currentY;
						}

					}

					element.bind('mousemove', move);

					function up(event) {
						// stop drawing
						drawing = false;
					}
					element.bind('mouseup', up);



					// Tablet
					function onTouchMove(event) {
						if (drawing) {
							event.preventDefault();
							if (event.offsetX !== undefined) {
								currentX = event.offsetX;
								currentY = event.offsetY;
							} else {
								currentX = (event.targetTouches[0].pageX) - element.offset().left;
								currentY = (event.targetTouches[0].pageY) - element.offset().top;
							}

							draw(lastX, lastY, currentX, currentY);

							// set current coordinates to last one
							lastX = currentX;
							lastY = currentY;
						}
					}

					function onTouchStart(event) {
						drawing = true;

						lastX = (event.targetTouches[0].pageX) - element.offset().left;
						lastY = (event.targetTouches[0].pageY) - element.offset().top;

						ctx.beginPath();
					}

					function onMouseUp(event) {
						drawing = false;
					}



					element.bind('touchmove', onTouchMove, false);
					element.bind('touchstart', onTouchStart, false);
					element.bind('touchend', onMouseUp, false);
					element[0].addEventListener('touchmove', onTouchMove, false);
					element[0].addEventListener('touchstart', onTouchStart, false);
					element[0].addEventListener('touchend', onMouseUp, false);
					// canvas reset
					function reset() {
						element[0].width = element[0].width;
					}

					function draw(lX, lY, cX, cY) {

						// line from
						ctx.moveTo(lX, lY);
						// to
						ctx.lineTo(cX, cY);
						// color
						ctx.strokeStyle = "#4bf";
						// draw it
						ctx.stroke();
					}
					console.log(element);
				}
			};
		});

}(angular.module("Argus")));
